mapper.sql:
SELECT
COUNT(1)
FROM
t_user u
WHERE 1 = 1

    AND u.username = #{user.username}


    AND u.mobile = #{user.mobile}


    And u.create_time > #{user.createTimeFrom}


    And u.create_time < #{user.createTimeTo}

SELECT u.user_id userId, u.username, u.email, u.mobile, u.status, u.create_time createTime, u.integral, u.last_login_time lastLoginTime, u.modify_time modifyTime, u.avatar FROM t_user u
AND u.username = #{user.username}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
And u.create_time > #{user.createTimeFrom}
And u.create_time < #{user.createTimeTo}
GROUP BY u.username, u.user_id, u.email, u.mobile, u.status, u.create_time, u.integral, u.last_login_time, u.modify_time, u.avatar
